<?phpif (!isset($_GET["sid"])) return;if (!isset($_GET["tid"])) return;if (!isset($_GET["g"])) return;if (!isset($_GET["n"])) return;$sid = $_GET["sid"];$tid = $_GET["tid"];$g = $_GET["g"];$n = $_GET["n"];if ($g < 0 || $g > 200 || !is_numeric($g)){	print("Invalid grade. Try again");	return;}$cn = mysql_connect("localhost","mtguser","mtg12345") or die(mysql_error());mysql_select_db("mtgdb",$cn);switch ($n){	case 0:		$s = "insert into tblgrades values(".$sid.",".$tid.",".$g.")";		$sm = "t_ok_1";		break;			case 1:		$s = "update tblgrades set grade=".$g." where studid=".$sid." and topicid=".$tid;		$sm = "t_ok_2";		break;			case 2:		$sm = "t_ok_3";		$s="delete from tblgrades where studid=".$sid." and topicid=".$tid;		break;}$r = mysql_query($s,$cn);if ($r) print($sm);else print("SAVE FAILED. TRY AGAIN.");mysql_close($cn);?>